SELECT l1_batch_number, status FROM proof_compression_jobs_fri
            WHERE l1_batch_number = ( SELECT MIN(l1_batch_number) FROM proof_compression_jobs_fri WHERE status = $1 OR status = $2
            )

ddb3b38be2b6038b63288961f46ba7d3bb7250ca.query:
SELECT circuit_type, result from prover_jobs
WHERE l1_batch_number = $1 AND status = 'successful' AND aggregation_round = $2
ORDER BY sequence_number ASC;
